import React, { useState, useEffect } from 'react';
import { Header } from '../src/Header';
import { Menu } from '../src/Menu';
import OpenCampaign from './OpenCampaign';
import axios from 'axios';
import CampaignManager from './components/CampaignManager/CampaignManager';
function Home() {
  const [campaign, setCampaign] = useState();
  const [parties, setParties] = useState();
  const [count, setCount] = useState();
  const [status, setStatus] = useState();
  const [admins, setAdmins] = useState();
  const getCampaignState = (name) =>
    axios.get(`http://127.0.0.1:5000/campaigns_state/${name}`).then((res) => {
      setParties(res.data.res.parties);
      setCount(res.data.res.count);
      setStatus(res.data.res.state);
      setAdmins(res.data.res.admins.split(','));
    });
  useEffect(() => {
    const updateCamapaigns = async () => {
      const res = await axios.get(
        `http://127.0.0.1:5000/batches/open_campaigns`,
      );
      setCampaigns(res.data.res);
    };
    updateCamapaigns();
  }, []);
  return (
    <div>
      <Header />
      <Menu />

      <div className="container">
        <div className="row">
          <div className="col-6">
            <h3>Manage Campaigns</h3>
            <OpenCampaign setCampaign={setCampaign} />
            {/* <SuspendCampaign
              campaigns={campaigns}
              setCampaigns={setCampaigns}
            /> */}
          </div>
          <div className="col-6">
            <h3>Campaigns List</h3>
            <ul>
              {campaigns.enabled.map(({ name, link }) => {
                return (
                  <li
                    key={`campaign-${name}`}
                    onClick={() => getCampaignState(name)}
                  >
                    {name}
                    {/* <a href={link}></a> */}
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
        <CampaignManager />
      </div>
    </div>
  );
}

export default Home;
