import React, { useState, useEffect } from 'react';
import { Header } from '../src/Header';
import { Menu } from '../src/Menu';
import OpenCampaign from './OpenCampaign';
import axios from 'axios';
import CampaignManager from './components/CampaignManager/CampaignManager';
import { toast, ToastContainer } from 'react-toastify';
function Home() {
  const [campaigns, setCampaigns] = useState({ enabled: [], disabled: [] });
  const [campaign, setCampaign] = useState();

  const getCampaignState = async (name) => {
    const res = await axios.get(
      `http://127.0.0.1:5000/campaigns_state/${name}`,
    );
    console.log(res);
    return res;
  };

  const openCampaignClickHandler = async (name) => {
    try {
      const serverRes = await axios.post(
        `http://127.0.0.1:5000/open_campaign?name=${name}`,
      );
      const { res, success } = serverRes.data;
      if (success) {
        const enabled = [...campaigns.enabled, { ...res }];
        setCampaigns({ ...campaigns, enabled });
        const state = getCampaignState(name).data.res;
        setCampaign({ ...state, ...res });
        toast.info(`Campaign ${name} has been added succesfully`);
      } else {
        toast.info(`An error has occured: `);
        console.log(res);
      }
    } catch (err) {
      console.log(err);
      toast.info(`An error has occured: ${err.message}`);
    }
  };

  useEffect(() => {
    const updateCamapaigns = async () => {
      const res = await axios.get(
        `http://127.0.0.1:5000/batches/open_campaigns`,
      );
      setCampaigns({ ...campaigns, enabled: res.data.res });
    };
    updateCamapaigns();
  }, []);
  return (
    <div>
      <Header />
      <Menu />

      <div className="container">
        {!campaign && (
          <div className="row">
            <div className="col-6">
              <h3>Manage Campaigns</h3>
              <OpenCampaign
                campaigns={campaigns}
                setCampaigns={setCampaigns}
                openCampaignClickHandler={openCampaignClickHandler}
              />
            </div>
            <div className="col-6">
              <h3>Campaigns List</h3>
              <ul>
                {campaigns.enabled.map(({ name, link }) => {
                  return (
                    <li key={`campaign-${name}`}>
                      <a href={link}>{name}</a>
                    </li>
                  );
                })}
              </ul>
            </div>
          </div>
        )}
        <CampaignManager {...campaign} />
      </div>
    </div>
  );
}

export default Home;
