import React, { useEffect, useState } from 'react';
import CampaignManagerStyles from './CampaignManager.module.scss';
import axios from 'axios';

const CampaignManager = () => {
  const props = {
    name: '2027',
  };
  const { name } = props;
  const [parties, setParties] = useState();
  const [count, setCount] = useState();
  const [status, setStatus] = useState();
  const [admins, setAdmins] = useState();

  axios.get(`http://127.0.0.1:5000/campaigns_state/${name}`).then((res) => {
    setParties(res.data.res.parties);
    setCount(res.data.res.count);
    setStatus(res.data.res.state);
    setAdmins(res.data.res.admins);
  });

  const statusOnClickHandler = async (event) => {
    console.log(event.target.innerText);
    // const status = event.target.innerText.toLowerCase();
    // const proxyRes = await axios.post(
    //   `http://127.0.0.1:5000/campaigns_state/${name}?status=${status}`,
    // );
    // const { data } = proxyRes;
    // const { success, res } = data;
  };
  return (
    <div>
      <div className="row" className={CampaignManagerStyles.section}>
        <div className="col-12">
          <h3 className={CampaignManagerStyles.header}>
            <span>{name}</span>
            {console.log(parties, count, status, admins)}
            <ul className={`list-inline ${CampaignManagerStyles.statusList}`}>
              {['setup', 'open', 'suspended', 'closed'].map((item) => (
                <li
                  key={item}
                  className={`${CampaignManagerStyles.status} list-inline-item`}
                  onClick={statusOnClickHandler}
                >
                  {item}
                </li>
              ))}
            </ul>
          </h3>
          <h6>Admins:</h6>
          <ul className="list-inline">
            <li className="list-inline-item">12-93ieiocdsc823d-9xjed8234-9d</li>
          </ul>
        </div>
      </div>
    </div>
  );
};
export default CampaignManager;
