import React, { useState } from 'react';
import { Header } from '../src/Header';
import { Menu } from '../src/Menu';
import OpenCampaign from './OpenCampaign';
function Home() {
  const [campaigns, setCampaigns] = useState([]);
  return (
    <div>
      <Header />
      <Menu />

      <div className="container">
        <div className="row">
          <div className="col-6">
            <h3>Manage Campaigns</h3>
            <OpenCampaign campaigns={campaigns} setCampaigns={setCampaigns} />
            <SuspendCampaign
              campaigns={campaigns}
              setCampaigns={setCampaigns}
            />
            <DCampaign campaigns={campaigns} setCampaigns={setCampaigns} />
          </div>
          <div className="col-6">
            <h3>Campaigns List</h3>
            <ul>
              {campaigns.map(({ name, res }) => {
                const { link } = res;
                return (
                  <li key={`campaign-${name}`}>
                    <a href={link}>{name}</a>
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
