import React, { useState, useEffect } from 'react';
import { Header } from '../src/Header';
import { Menu } from '../src/Menu';
import OpenCampaign from './OpenCampaign';
import axios from 'axios';

function Home() {
  const [campaigns, setCampaigns] = useState({ enabled: [], disabled: [] });
  useEffect(() => {
    const updateCamapgins = async () => {
      const res = await axios.get(
        `${process.env.HOST}:${process.env.PORT}/campaigns`,
      );
      setCampaigns(res.data.res);
    };
    updateCamapgins();
  }, []);
  return (
    <div>
      <Header />
      <Menu />

      <div className="container">
        <div className="row">
          <div className="col-6">
            <h3>Manage Campaigns</h3>
            <OpenCampaign campaigns={campaigns} setCampaigns={setCampaigns} />
            {/* <SuspendCampaign
              campaigns={campaigns}
              setCampaigns={setCampaigns}
            /> */}
          </div>
          <div className="col-6">
            <h3>Campaigns List</h3>
            <ul>
              {campaigns.enabled.map(({ name, link }) => {
                return (
                  <li key={`campaign-${name}`}>
                    <a href={link}>{name}</a>
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
